from dhcppython.packet import DHCPPacket
from ipaddress import ip_interface
from os.path import getsize
from socket import gethostname

unicast = '0.0.0.0'
siaddr = '192.168.0.8'
mask = '255.255.255.0'
router = '192.168.0.251'
dns = '223.5.5.5'
broadcast = ip_interface(f'{siaddr}/{mask}').network.broadcast_address
lease_time = 120

file = 'ipxe-x86_64.efi'

class udp_server:
    def __init__(self, proxy_mode=False):
        self.proxy_mode = proxy_mode
    def start(self, packet_offer, packet_ack):
        self.dhcpd(packet_offer)
        self.proxy_dhcpd(packet_ack)
    def dhcpd(self, packet):
        print('dhcpd (0.0.0.0, 67) started...')
        if self.proxy_mode:
            if packet.msg_type == 'DHCPOFFER':
                print(f'{packet.msg_type} received MAC {packet.chaddr.lower()} XID {hex(packet.xid)}')
                print(f'{packet.msg_type} send to ({broadcast}, 68) yiaddr={unicast}, siaddr={siaddr}, sname={gethostname()}, file={file}, file_size={round(getsize(file)/1024, ndigits = None)*2}, router={router}, dns={dns}, lease_time={lease_time}')
    def proxy_dhcpd(self, packet):
        print(f'dhcpd ({siaddr}, 4011) started...')
        if self.proxy_mode:
            if packet.msg_type == 'DHCPACK':
                print(f'{packet.msg_type} received MAC {packet.chaddr.lower()} XID {hex(packet.xid)}')
                print(f'{packet.msg_type} send to ({siaddr}, 68) yiaddr={unicast}, siaddr={siaddr}, sname={gethostname()}, file={file}, file_size={round(getsize(file)/1024, ndigits = None)*2}')
